import React from 'react';
import FundoAwards from '../images/FundoAwards.png';

function AwardsPage() {
    return (
        <section id="AwardsPage" className="w-full min-h-screen ">
            <div className="bg-gradient-to-b from-magic-200 to-magic-400 z-[99]">
                <div className=" bg-center bg-cover min-h-screen w-full" style={{ backgroundImage: `url(${FundoAwards})`}}>
                    <div className="w-full text-5xl text-offwhite flex items-center justify-center pt-8 font-bold">
                        <h1 className="border-b-8 w-auto border-offwhite rounded-lg opacity-100">Por que escolher a <span className="bg-offwhite text-magic-200 rounded-t-lg rounded-ee-lg">Magic?</span></h1>
                    </div>
                </div>
                <p className="text-center px-32 py-14 text-white text-2xl">Se a sua empresa enfrenta desafios na criação de soluções tecnológicas devido à falta de especialistas, o Codi Magic é a escolha ideal para você. Combinando rapidez, preços acessíveis e garantia total, oferecemos uma experiência de desenvolvimento sem complicações. Escolha o Codi Magic e transforme suas ideias em realidade, sem se preocupar com a complexidade do desenvolvimento de software. Veja o que já consquistamos:</p>
            </div>
            <div className="w-full min-h-screen bg-gradient-to-b from-magic-400 to-magic-500">
                {/* implemente a segunda parte aqui */}
            </div>
            
        </section>
    );
}

export default AwardsPage;
